import React from "react";
import { FaTimes } from "react-icons/fa";

const Task = ({ task, onDelete }) => {
  return (
    <div
      className="task"
      onDoubleClick={() => {
        onDelete(task.id);
      }}
    >
      <h3>
        {" "}
        {task.text} <FaTimes style={{ color: "red", cursor: "pointer" }} />{" "}
      </h3>
      <p> {task.day} </p>
    </div>
  );
};

export default Task;
